import React, { useRef, useState } from 'react'

const VDOM_crud = () => {
    const FNAME = useRef()
    const LNAME = useRef()
    const [data, setdata] = useState([])
    const [view, setview] = useState({})
    const [ind, setind] = useState()

    const submit_fn = () => {
        const input = {
            FNAME: FNAME.current.value,
            LNAME: LNAME.current.value,
        }
        setdata([...data, input])
    }

    const delete_fn = (ind) => {
        data.splice(ind, 1)
        setdata([...data])
    }

    const update_fn = (val, ind) => {
        setview(val)
        setind(ind)
    }

    const save_update = () => {
        console.log(ind);
        console.log(view);
        data.splice(ind, 1, view)
        setdata([...data])
    }

    return (
        <>
            <div>
                <label>FNAME</label>
                <input name='FNAME' ref={FNAME} />
                <label>LNAME</label>
                <input name='LNAME' ref={LNAME} />
            </div>
            <div>
                <button onClick={submit_fn}>SUBMIT</button>
            </div>
            <div>
                {
                    data?.map((val, ind) => {
                        return (
                            <>
                                <table border={5}>
                                    <tr>
                                        <h1>{val.FNAME}</h1>
                                        <td><h1>{val.LNAME}</h1></td>
                                        <td><button onClick={() => delete_fn(ind)}>DELETE</button></td>
                                        {/* onClick={() => update_fn(val, ind) */}
                                    </tr>
                                </table>
                            </>
                        )
                    })
                }
            </div>

            {/* MODAL */}
            <button type="button" class="py-3 px-4 inline-flex items-center gap-x-2 text-sm font-semibold rounded-lg border border-transparent bg-blue-600 text-white hover:bg-blue-700 disabled:opacity-50 disabled:pointer-events-none dark:focus:outline-none dark:focus:ring-1 dark:focus:ring-gray-600" data-hs-overlay="#hs-basic-modal">
                Open modal
            </button>

            <div id="hs-basic-modal" class="hs-overlay hs-overlay-open:opacity-100 hs-overlay-open:duration-500 hidden size-full fixed top-0 start-0 z-[80] opacity-0 overflow-x-hidden transition-all overflow-y-auto pointer-events-none">
                <div class="hs-overlay-open:opacity-100 hs-overlay-open:duration-500 opacity-0 transition-all sm:max-w-lg sm:w-full m-3 sm:mx-auto">
                    <div class="flex flex-col bg-white border shadow-sm rounded-xl pointer-events-auto dark:bg-gray-800 dark:border-gray-700 dark:shadow-slate-700/[.7]">
                        <div class="flex justify-between items-center py-3 px-4 border-b dark:border-gray-700">
                            <h3 class="font-bold text-gray-800 dark:text-white">
                                Modal title
                            </h3>
                            <button type="button" class="flex justify-center items-center size-7 text-sm font-semibold rounded-full border border-transparent text-gray-800 hover:bg-gray-100 disabled:opacity-50 disabled:pointer-events-none dark:text-white dark:hover:bg-gray-700 dark:focus:outline-none dark:focus:ring-1 dark:focus:ring-gray-600" data-hs-overlay="#hs-basic-modal">
                                <span class="sr-only">Close</span>
                                <svg class="flex-shrink-0 size-4" xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><path d="M18 6 6 18" /><path d="m6 6 12 12" /></svg>
                            </button>
                        </div>
                        <div class="p-4 overflow-y-auto">
                            <p class="mt-1 text-gray-800 dark:text-gray-400">
                                This is a wider card with supporting text below as a natural lead-in to additional content.
                            </p>
                        </div>
                        <div class="flex justify-end items-center gap-x-2 py-3 px-4 border-t dark:border-gray-700">
                            <button type="button" class="py-2 px-3 inline-flex items-center gap-x-2 text-sm font-medium rounded-lg border border-gray-200 bg-white text-gray-800 shadow-sm hover:bg-gray-50 disabled:opacity-50 disabled:pointer-events-none dark:bg-slate-900 dark:border-gray-700 dark:text-white dark:hover:bg-gray-800 dark:focus:outline-none dark:focus:ring-1 dark:focus:ring-gray-600" data-hs-overlay="#hs-basic-modal">
                                Close
                            </button>
                            <button type="button" class="py-2 px-3 inline-flex items-center gap-x-2 text-sm font-semibold rounded-lg border border-transparent bg-blue-600 text-white hover:bg-blue-700 disabled:opacity-50 disabled:pointer-events-none dark:focus:outline-none dark:focus:ring-1 dark:focus:ring-gray-600">
                                Save changes
                            </button>
                        </div>
                    </div>
                </div>
            </div>
            {/* onClick={save_update} */}
        </>
    )
}

export default VDOM_crud
