import axios from 'axios'
import React, { useRef, useState } from 'react'

const Full_stack_crud = () => {
    const first_name = useRef()
    const last_name = useRef()
    const email = useRef()
    const password = useRef()
    const confirm_password = useRef()
    const [input, setinput] = useState({})
    const [data, setdata] = useState([])
    const submit_handler = async () => {
        const input = {
            first_name: first_name.current.value,
            last_name: last_name.current.value,
            email: email.current.value,
            password: password.current.value,
            confirm_password: confirm_password.current.value,
        }
        setinput(input)
        await axios.post(
            "http://localhost:5500/v1/user/create", input)
            .then((res) => console.log(res))
            .catch((err) => console.log(err))
    }
    const API_handler = async () => {
        await axios.get("http://localhost:5500/v1/user/list", {
            headers: { "content-type": "application/json" }
        })
            .then((res) => {
                setdata([...res.data])
                console.log(data)
            })
            .catch((err) => console.log("EREERERR", err))
        // await axios.get("http://192.168.235.252:5500/v1/user/list")
        //     .then((res) => console.log("REEEEEESSSSS" + res))
        //     .catch((err) => console.log("EREERERR" + err))
        // await axios.post("http://192.168.235.252:5500/v1/user/create")
        //     .then((res) => console.log("REEEEEESSSSS" + res))
        //     .catch((err) => console.log("EREERERR" + err))
        // axios.post("http://locahost:5500/v1/user/create").then((res) => console.log(res)).catch((err) => console.log(err))
        // const data = await fetch('https://dummyjson.com/todos')
        //     .then(res => { res.json(); console.log(res); })
        //     .then(console.log);
    }
    return (
        <>
            <center>
                <div style={{ marginTop: "150px" }}>
                    <div>
                        <label>first_name</label>
                        <input ref={first_name} type='text' />
                    </div>
                    <div>
                        <label>last_name</label>
                        <input ref={last_name} type='text' />
                    </div>
                    <div>
                        <label>email</label>
                        <input ref={email} type='email' required />
                    </div>
                    <div>
                        <label>password</label>
                        <input ref={password} type='text' />
                    </div>
                    <div>
                        <label>confirm password</label>
                        <input ref={confirm_password} type='text' />
                    </div>
                    <div>
                        <button onClick={submit_handler}>SUBMIT</button>
                        <button onClick={API_handler}>Call API</button>
                    </div>
                </div>
            </center>
            {
                data?.map((val) => {
                    return (
                        <>
                            <h1>{val.first_name}</h1>
                            <h2>{val.last_name}</h2>
                        </>
                    )
                })
            }
        </>
    )
}

export default Full_stack_crud
