import React, { useRef, useState } from 'react'
import toggleModal from '../tailwind'

const VDOM_crud = () => {
    const FNAME = useRef()
    const LNAME = useRef()
    const [data, setdata] = useState([])
    const [view, setview] = useState({})
    const [ind, setind] = useState()

    const submit_fn = () => {
        const input = {
            FNAME: FNAME.current.value,
            LNAME: LNAME.current.value,
        }
        setdata([...data, input])
    }

    const delete_fn = (ind) => {
        data.splice(ind, 1)
        setdata([...data])
    }

    const update_fn = (val, ind) => {
        setview(val)
        setind(ind)
    }

    const save_update = () => {
        console.log(ind);
        console.log(view);
        data.splice(ind, 1, view)
        setdata([...data])
    }

    return (
        <>
            <div>
                <label>FNAME</label>
                <input name='FNAME' ref={FNAME} />
                <label>LNAME</label>
                <input name='LNAME' ref={LNAME} />
            </div>
            <div>
                <button onClick={submit_fn}>SUBMIT</button>
            </div>
            <div>
                {
                    data?.map((val, ind) => {
                        return (
                            <>
                                <table border={5}>
                                    <tr>
                                        <h1>{val.FNAME}</h1>
                                        <td><h1>{val.LNAME}</h1></td>
                                        <td><button onClick={() => delete_fn(ind)}>DELETE</button></td>
                                        {/* onClick={() => update_fn(val, ind) */}
                                    </tr>
                                </table>
                            </>
                        )
                    })
                }
            </div>

            {/* MODAL */}
            <div class="flex items-center justify-center h-screen">
                <button class="py-2 px-6 bg-blue-500 text-white rounded hover:bg-blue-700 transition font-medium duration-500" onclick={toggleModal()}>SHOW MODAL</button>
            </div>
            <div class="fixed z-10 overflow-y-auto top-0 w-full left-0 hidden" id="modal">
                <div class="flex items-center justify-center min-height-100vh pt-4 px-4 pb-20 text-center sm:block sm:p-0">
                    <div class="fixed inset-0 transition-opacity">
                        <div class="absolute inset-0 bg-gray-900 opacity-75" />
                    </div>
                    <span class="hidden sm:inline-block sm:align-middle sm:h-screen">&#8203;</span>
                    <div class="inline-block align-center bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full" role="dialog" aria-modal="true" aria-labelledby="modal-headline">
                        <div class="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                            <label class="font-medium text-gray-800">Name</label>
                            <input type="text" class="w-full outline-none rounded bg-gray-100 p-2 mt-2 mb-3" />
                            <label class="font-medium text-gray-800">Url</label>
                            <input type="text" class="w-full outline-none rounded bg-gray-100 p-2 mt-2 mb-3" />
                        </div>
                        <div class="bg-gray-200 px-4 py-3 text-right">
                            <button type="button" class="py-2 px-4 bg-gray-500 text-white rounded hover:bg-gray-700 mr-2" onclick="toggleModal()"><i class="fas fa-times"></i> Cancel</button>
                            <button type="button" class="py-2 px-4 bg-blue-500 text-white rounded font-medium hover:bg-blue-700 mr-2 transition duration-500"><i class="fas fa-plus"></i> Create</button>
                        </div>
                    </div>
                </div>
            </div>
            {/* onClick={save_update} */}
        </>
    )
}

export default VDOM_crud
