import React, { useRef, useState } from 'react'

const VDOM_crud = () => {
    const FNAME = useRef()
    const LNAME = useRef()
    const [data, setdata] = useState([])

    const submit_fn = () => {
        const input = {
            FNAME: FNAME.current.value,
            LNAME: LNAME.current.value,
        }
        setdata([...data, input])
    }

    const delete_fn = (ind) => {
        data.splice(ind, 1)
        setdata([...data])
    }

    return (
        <>
            <div>
                <label>FNAME</label>
                <input name='FNAME' ref={FNAME} />
                <label>LNAME</label>
                <input name='LNAME' ref={LNAME} />
            </div>
            <div>
                <button onClick={submit_fn}>SUBMIT</button>
            </div>
            <div>
                {
                    data?.map((val, ind) => {
                        return (
                            <>
                                <h1>{val.FNAME}</h1>
                                <h1>{val.LNAME}</h1>
                                <button onClick={() => delete_fn(ind)}>DELETE</button>
                            </>
                        )
                    })
                }
            </div>
        </>
    )
}

export default VDOM_crud
