import React, { useRef, useState } from 'react'

const VDOM_crud = () => {
    const FNAME = useRef()
    const LNAME = useRef()
    const [data, setdata] = useState([])
    const [view, setview] = useState({})
    const [ind, setind] = useState()

    const submit_fn = () => {
        const input = {
            FNAME: FNAME.current.value,
            LNAME: LNAME.current.value,
        }
        setdata([...data, input])
    }

    const delete_fn = (ind) => {
        data.splice(ind, 1)
        setdata([...data])
    }

    const update_fn = (val, ind) => {
        setview(val)
        setind(ind)
    }

    const save_update = () => {
        console.log(ind);
        console.log(view);
        data.splice(ind, 1, view)
        setdata([...data])
    }

    return (
        <>
            <div>
                <label>FNAME</label>
                <input name='FNAME' ref={FNAME} />
                <label>LNAME</label>
                <input name='LNAME' ref={LNAME} />
            </div>
            <div>
                <button onClick={submit_fn}>SUBMIT</button>
            </div>
            <div>
                {
                    data?.map((val, ind) => {
                        return (
                            <>
                                <table border={5}>
                                    <tr>
                                        <h1>{val.FNAME}</h1>
                                        <td><h1>{val.LNAME}</h1></td>
                                        <td><button onClick={() => delete_fn(ind)}>DELETE</button></td>
                                        {/* onClick={() => update_fn(val, ind) */}
                                    </tr>
                                </table>
                            </>
                        )
                    })
                }
            </div>

            {/* MODAL */}
            <div class="bg-gray-600 text-gray-100 js-carousel">
                <div class="p-20 py-20 w-11/12 mx-auto flex flex-col gap-9">
                    <div class="flex flex-col gap-4">
                        <h1 class="text-4xl font-bold">Modal carousel / Tailwind <span class="text-blue-200">CSS</span>
                        </h1>
                        <span class="font-semibold">Modal created without using JS. Note: I used a more advanced version of this on a live site, the customer wanted the text height to be the same as the image (overflow-y-scroll); I encountered issues when using lazy-load for the images since it wouldn't give a rendered height (Safari/ Firefox) - disable lazyload for modal images.</span>
                    </div>

                    <dialog id="firstModal" class="p-10 border-2 border-gray-300 w-1/2 mx-auto">
                        <div class="grid grid-cols-2 gap-6">
                            <div class="bg-blue-300 w-full h-full rounded-md"></div>
                            <div class="flex flex-col gap-4">
                                <div>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</div>
                                <button onclick="firstModal.close()" class="bg-white hover:bg-gray-100 text-gray-800 font-semibold py-2 px-4 w-full border border-gray-400 rounded shadow">
                                    Close modal
                                </button>
                            </div>
                        </div>
                    </dialog>

                    <div class="flex flex-row justify-center text-gray-900">

                        <div class="flex flex-col w-1/2">
                            <div class="bg-gray-300 w-full h-40"></div>
                            <div class="bg-gray-200 w-full p-4 border-l-2 border-r-2 border-b-2 border-gray-400 grid grid-cols-2 gap-6 items-end gap-2">
                                <div>
                                    <div class="flex flex-col">
                                        <span class="font-bold text-2xl">Modal title</span>
                                        <span class="font-bold text-xl">Modal Lead</span>
                                    </div>
                                    <span class="">Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam</span>
                                </div>
                                <div><button onclick="firstModal.showModal()" class="bg-white hover:bg-gray-100 text-gray-800 font-semibold py-2 px-4 w-full border border-gray-400 rounded shadow">
                                    Open modal
                                </button></div>
                            </div>
                        </div>

                    </div>

                </div>
            </div>
            {/* onClick={save_update} */}
        </>
    )
}

export default VDOM_crud
