import axios from 'axios'
import React, { useEffect, useRef, useState } from 'react'

const App1 = () => {
    const fname = useRef()
    const lname = useRef()
    const [Data, setData] = useState([])
    const [view, setview] = useState({})
    const [ind, setind] = useState({})
    useEffect(() => {
        axios.get("http://localhost:3001/posts").then((res) => { console.log(res.data); setData(res.data) }).catch((err) => { console.log(err) })
    }, [])
    const submit_handler = () => {
        const input = {
            fname: fname.current.value,
            lname: lname.current.value,
        }
        axios.post("http://localhost:3001/posts", input).then((res) => console.log(res)).catch((err) => console.log(err))
        setData([...Data, input])
    }
    const delete_handler = (val, ind) => {
        axios.delete("http://localhost:3001/posts", val.id).then((res) => console.log(res)).catch((err) => console.log(err))
        Data.splice(ind, 1)
        setData([...Data])
    }
    const update_input = (e) => {
        setview({ ...view, [e.target.name]: e.target.value })
        console.log(view)
    }
    const update_handler = () => {
        Data.splice(ind, 1, view)
        setData([...Data])
    }
    return (
        <>
            <div>
                <label>fname</label>
                <input name="fname" ref={fname} />
                <label>lname</label>
                <input name="lname" ref={lname} />
                <button onClick={submit_handler}>submit</button>
            </div>
            <div>
                <label>fname</label>
                <input name="fname" value={view.fname} onChange={update_input} />
                <label>lname</label>
                <input name="lname" value={view.lname} onChange={update_input} />
                <button onClick={update_handler}>Update</button>
            </div>
            {
                Data?.map((a, b, c) => {
                    return (
                        <>
                            <h1>{a.fname}</h1>
                            <h1>{a.lname}</h1>
                            <button onClick={() => delete_handler(a, b)}>Delete</button>
                            <button onClick={() => { setview(a); setind(b) }}>Change</button>
                        </>
                    )
                })
            }
        </>
    )
}

export default App1
