import React, { useRef, useState } from 'react'

const VDOM_crud = () => {
    const FNAME = useRef()
    const LNAME = useRef()
    const [data, setdata] = useState([])
    const [view, setview] = useState({})
    const [ind, setind] = useState()

    const submit_fn = () => {
        const input = {
            FNAME: FNAME.current.value,
            LNAME: LNAME.current.value,
        }
        setdata([...data, input])
    }

    const delete_fn = (ind) => {
        data.splice(ind, 1)
        setdata([...data])
    }

    const update_fn = (val, ind) => {
        setview(val)
        setind(ind)
    }

    const save_update = () => {
        console.log(ind);
        console.log(view);
        data.splice(ind, 1, view)
        setdata([...data])
    }

    return (
        <>
            <div>
                <label>FNAME</label>
                <input name='FNAME' ref={FNAME} />
                <label>LNAME</label>
                <input name='LNAME' ref={LNAME} />
            </div>
            <div>
                <button onClick={submit_fn}>SUBMIT</button>
            </div>
            <div>
                {
                    data?.map((val, ind) => {
                        return (
                            <>
                                <table border={5}>
                                    <tr>
                                        <h1>{val.FNAME}</h1>
                                        <td><h1>{val.LNAME}</h1></td>
                                        <td><button onClick={() => delete_fn(ind)}>DELETE</button></td>
                                        {/* onClick={() => update_fn(val, ind) */}
                                    </tr>
                                </table>
                            </>
                        )
                    })
                }
            </div>

            {/* MODAL */}
            <div class="relative z-10" aria-labelledby="slide-over-title" role="dialog" aria-modal="true">

                <div class="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity"></div>

                <div class="fixed inset-0 overflow-hidden">
                    <div class="absolute inset-0 overflow-hidden">
                        <div class="pointer-events-none fixed inset-y-0 right-0 flex max-w-full pl-10">

                            <div class="pointer-events-auto relative w-screen max-w-md">

                                <div class="absolute left-0 top-0 -ml-8 flex pr-2 pt-4 sm:-ml-10 sm:pr-4">
                                    <button type="button" class="relative rounded-md text-gray-300 hover:text-white focus:outline-none focus:ring-2 focus:ring-white">
                                        <span class="absolute -inset-2.5"></span>
                                        <span class="sr-only">Close panel</span>
                                        <svg class="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" aria-hidden="true">
                                            <path stroke-linecap="round" stroke-linejoin="round" d="M6 18L18 6M6 6l12 12" />
                                        </svg>
                                    </button>
                                </div>

                                <div class="flex h-full flex-col overflow-y-scroll bg-white py-6 shadow-xl">
                                    <div class="px-4 sm:px-6">
                                        <h2 class="text-base font-semibold leading-6 text-gray-900" id="slide-over-title">Panel title</h2>
                                    </div>
                                    <div class="relative mt-6 flex-1 px-4 sm:px-6">
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* onClick={save_update} */}
        </>
    )
}

export default VDOM_crud
