import React, { useEffect, useRef, useState } from 'react'

const Localstore_crud = () => {
    const FNAME = useRef()
    const LNAME = useRef()
    const [data, setdata] = useState([])
    const [filterdata, setfilterdata] = useState([])
    const [view, setview] = useState({})
    const [ind, setind] = useState()

    useEffect(() => {
        const local_data = JSON.parse(localStorage.getItem("Data")) || []
        setdata(local_data)
        setfilterdata(data)
    }, [])
    console.log(data);
    console.log(filterdata);

    const submit_fn = () => {
        const input = {
            FNAME: FNAME.current.value,
            LNAME: LNAME.current.value,
        }
        setdata([...data, input])
        console.log("data", data);
        setfilterdata([...data])
        console.log("filterdata", filterdata);
        localStorage.setItem("Data", JSON.stringify(data))
    }

    const delete_fn = (ind) => {
        data.splice(ind, 1)
        setdata([...data])
        setfilterdata([...data])
    }

    const update_fn = (val, ind) => {
        setview(val)
        setind(ind)
    }

    const save_update = () => {
        console.log(ind);
        console.log(view);
        data.splice(ind, 1, view)
        setdata([...data])
        setfilterdata([...data])
    }

    return (
        <>
            <div>
                <label>FNAME</label>
                <input name='FNAME' ref={FNAME} />
                <label>LNAME</label>
                <input name='LNAME' ref={LNAME} />
            </div>
            <div>
                <button onClick={submit_fn}>SUBMIT</button>
            </div>
            <div>
                {
                    filterdata?.map((val, ind) => {
                        return (
                            <>
                                <table border={5}>
                                    <tr>
                                        <h1>{val.FNAME}</h1>
                                        <td><h1>{val.LNAME}</h1></td>
                                        <td><button onClick={() => delete_fn(ind)}>DELETE</button></td>
                                        <td>{/* <button onClick={() => update_fn(val, ind)}>UPDATE</button> */}</td>
                                        <td><button onClick={() => update_fn(val, ind)} type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModal">UPADTE</button></td>
                                    </tr>
                                </table>
                            </>
                        )
                    })
                }
            </div>

            {/* MODAL */}


            <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div class="modal-dialog" role="document">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
                            <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div class="modal-body">
                            <div>
                                <label>FNAME</label>
                                <input name='FNAME' onChange={(e) => { setview({ ...view, FNAME: e.target.value }); console.log(view); }} value={view.FNAME} />
                            </div>
                            <div>
                                <label>LNAME</label>
                                <input name='LNAME' onChange={(e) => { setview({ ...view, LNAME: e.target.value }); console.log(view); }} value={view.LNAME} />
                            </div>
                        </div>
                        <div class="modal-footer">
                            <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                            <button type="button" class="btn btn-primary" onClick={save_update}>Save changes</button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Localstore_crud
